import React from 'react';
//import logo from './logo.svg';
//import '../index.css';
import Header from './Header.js';
import Main from './Main.js'
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js'
import { api } from '../utils/api.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js'
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import AddPlacePopup from './AddPlacePopup.js'

function App() {

  // переменные состояния для открытия попапов

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false)
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false)
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false)

  const [selectedCard, setSelectedCard] = React.useState({})

  // стейт отвечающий за данные текущего пользователя

  const [currentUser, setCurrentUser] = React.useState({});

  // переменные состояния для карточек

  const [cards, setCards] = React.useState([])


  // промис
  React.useEffect(()=> {
    Promise.all([api.getUserInfo(), api.getInitialCards()])
    .then(([profile, cards]) => {
      setCurrentUser(profile);
      setCards(cards);
    })
    .catch(err => {
        console.log(err)
    })
  }, [])

  // загрузка первоначльной коллекции карточек, один раз при сборке

  React.useEffect(() => {
    api.getInitialCards()
    .then(data => {
      setCards(
        data.map((item) => ({
          title: item.name,
          alt: item.name,
          url: item.link,
          like: item.likes.length,
          likes: item.likes,
          _id: item._id
        }))
      );
    })
    .catch(err => {
      console.log(err)
    })
  }, [])

  function handleAddPlaceSubmit(newCard) {
    api.addCard(newCard)
      .then((newCard) => {
        setCards([newCard, ...cards]);
      })
      .catch(err => {
        console.log(err)
      })
      .finally(() => closeAllPopups())
  }



  function handleCardLike(card) {
    // проверка есть ли лайк на текущей карточке
    // возвращает тру если среди лайкoв есть лайк пользователя
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    console.log(isLiked)

    // если пользователь не поставил лайк, то мы должны его добавить
    if (!isLiked) {
      api
        .likeCard(card._id)
        .then((newCard) => {
          const newCards = cards.map((c) => (c._id === card._id ? newCard : c));
          setCards(newCards);
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      api
        .deleteLikeCard(card._id)
        .then((newCard) => {
          const newCards = cards.map((c) => (c._id === card._id ? newCard : c));
          setCards(newCards);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }


  function handleCardDelete(card) {
    api.deleteCards(card._id)
      .then(() => {
        const newCards = cards.filter((c) => c._id !== card._id);
        setCards(newCards);
      })
      .catch(err => {
        console.log(err)
      })
  }


  React.useEffect(() => {
    api.getUserInfo()
      .then((profile) => {
        setCurrentUser(profile)
      })
      .catch(err => {
        console.log(err)
      })
  })

  // функции открытия попапов

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
  }

  // функция закрытия попапов

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsEditAvatarPopupOpen(false)
    setSelectedCard({})
  }

  function handleCardClick(card) {
    setSelectedCard(card)
  }

  function handleUpdateUser(user) {
    api.editUserInfo(user)
      .then((user) => setCurrentUser(user))
      .catch(err => {
        console.log(err)
      })
      .finally(() => closeAllPopups());
  }

  function handleUpdateAvatar(item) {
    api.editAvatar(item)
      .then((res) => {
        setCurrentUser(res)
      })
      .catch(err => {
        console.log(err)
      })
      .finally(() => closeAllPopups())
  }





  return (
    // внедряем даные с помощбю провадера контекста, всему поддереву теперь доступен этот контекст. 
    // То есть мы подключили дерево компонентов к провайдеру контекста
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
        <Main onEditProfile={handleEditProfileClick}
              onAddPlace={handleAddPlaceClick}
              onEditAvatar={handleEditAvatarClick}
              onCardClick = {handleCardClick}
              onCardLike = {handleCardLike}
              onCardDelete = {handleCardDelete}
              cards = {cards}

        />
        <Footer />

        <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} onUpdateUser={handleUpdateUser}></EditProfilePopup>

        <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} addNewCard={handleAddPlaceSubmit} onAddPlace={handleAddPlaceSubmit}></AddPlacePopup>

        <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} onUpdateAvatar={handleUpdateAvatar}></EditAvatarPopup>

        <PopupWithForm name='-delete' title='Вы уверены?' buttonText='Да' />

        <ImagePopup card={selectedCard} isOpen={selectedCard.url} onClose={closeAllPopups} />

      </div>
    </CurrentUserContext.Provider>

  );
}

export default App;

// при удалении карточки вылезает сначала белая карточки и ошибка про ключи
// при добавлении карточки вылезает сначала белая карточка и ошибка про ключи, после обновления карточка появляется
// невозможно изменить инпуты
