import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import PopupWithForm from './PopupWithForm.js';

function EditAvatarPopup(props) {
    return(
        <PopupWithForm name='-avatar' title='Обновить аватар' isOpen={props.isOpen} onClose={props.onClose} buttonText='Сохранить'>
          <input id='link-input' type="url" className="popup__input popup__input_avatar_link" name="link"
                      placeholder="Ссылка на картинку"/>
          <span className='error link-input-error'></span>
          </PopupWithForm>
    )
}

export default EditAvatarPopup;
