import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Card(props) {
  // подписываем компонент на контекст
  const currentUser = React.useContext(CurrentUserContext);

  // определяем, являемся ли мы владельцем текущей карточки
  const isOwn = card.owner._id === currentUser._id;

  // переменная с помощью которой определяется видиомсть иконки удаления на карточке

  const cardDeleteButtonClassName = (
    `element__delete ${isOwn ? ' ' : 'element__delete_disabled'}`
  ); 

  // определяем есть ли у карточки лайк
  const isLiked = card.likes.some(i => i._id === currentUser._id);

  // переменная с помощью которой определяется стоит лайк или нет

  const cardLikeButtonClassName = (
    `element__likes ${isLiked ? 'element__button_active' : ' '}`
  )


  function handleClick() {
    props.onCardClick(props.card);
  } 

  function handleLikeClick() {
    props.onCardLike(props.card)
  }

    return (
        <section className="elements">
              <article className="element">
                <button className="element__delete" type="button"></button>
                <img src={props.card.url} className="element__photo" alt={props.card.alt} onClick={handleClick}/>         
                 <div className="element__container">
                    <h2 className="element__title">{props.card.title}</h2>
                    <div className='element__likes'>
                        <button className="element__button" type="button" onClick={props.onCardLike}></button>
                        <p className='element__counter'>{props.card.like}</p>
                    </div>
                  </div>
              </article>
        </section>

    );
  }


  export default Card
