import React from "react";
import Header from "./Header";

function Register() {
    const [email, setEmail] = React.useState('')
    const [password, setPassword] = React.useState('')

    // обработчик изменения инпутов
    function handleMailChange(evt) {
        setEmail(evt.target.value)
    }

    function handlePasswordChange(evt) {
        setPassword(evt.target.value)
    }

    function handleSubmit(evt) {
        evt.preventDefault();

    }




    return(
        <>

    <Header mail='' title='Войти'  />
    <div className='main-page'>
        <h2 className='main-page__title'>Регистрация</h2>
        <form className="main-page__form" onClick={handleSubmit}>
            <input className="main-page__input"  id="reg-email-input" type="email" name='email' placeholder="Email"></input>
            <input className="main-page__input"  id="reg-password-input" type="password" name='password' placeholder="Пароль"></input>
            <button className="main-page__button" type="submit">Зарегистрироваться</button>
        </form>
        <div className='main-page__subtitle'>Уже зарегистрированы? Войти</div>
    </div>
    </>
    )
}

export default Register;
