import React from 'react';
import avatarUser from '../images/jak_kusto.jpg'
import pencilLogo from '../images/pancil.svg'
import plusLogo from '../images/plus.svg'
import Card from './Card.js';
import {api} from '../utils/api.js'
import { CurrentUserContext } from '../contexts/CurrentUserContext';




function Main(props) {
  // подпишем компонент на контекст
  const currentUser = React.useContext(CurrentUserContext);


  // переменные состояния для карточек

  const [cards, setCards] = React.useState([])


  function handleCardLike(card) {
    // проверка есть ли лайк на текущей карточке
    // возвращает тру если среди лайкoв есть лайк пользователя
    const isLiked = card.likes.find(card => card._id === currentUser._id);

  //  this._isLiked = data.likes.find(data => this._userId === data._id);


    // если пользователь не поставил лайк, то мы его добавляем
      if (!isLiked()) {
        api.likeCard(card._id)
        .then((card) => {
            card.updateLikes(card)
        })
        .catch(err => {
          console.log(err)
        })
    }
    // если лайк уже стоит, то при нажатии он должен удалиться
      else {
        api.deleteLikeCard(cards._id)
        .then((cards) => {
            card.updateLikes(card)
        })
        .catch(err => {
          console.log(err)
        })
    }
  }


  // загрузка первоначльной коллекции карточек, один раз при сборке

  React.useEffect(() => {
    api.getInitialCards()
    .then(data => {
      setCards(
        data.map((item) => ({
          id: item._id,
          title: item.name,
          alt: item.name,
          url: item.link,
          like: item.likes.length
        }))
      );
    })
    .catch(err => {
      console.log(err)
    })
  }, [])


    return (
        <main className="content">

        <section className="profile">
            <button className="profile__avatar-button" onClick={props.onEditAvatar}>
              <img src={currentUser.avatar} className="profile__avatar" alt="Аватар пользователя"/>
            </button>

          <div className="profile__info">
            <h1 className="profile__title">{currentUser.name}</h1>
              <button className="profile__button" type='button' onClick={props.onEditProfile}>
                <img src={pencilLogo} className="profile__image" alt="Карандаш"/>
              </button>          
            <p className="profile__subtitle">{currentUser.about}</p>
          </div>
          <button className="profile__addbutton" type='button' onClick={props.onAddPlace}>
                <img src={plusLogo} className="profile__image" alt="Плюс"/>
          </button>  
        </section>
        <section className="elements">
            {cards.map((item) => {
              return ( <Card key={item.id}
                             card={item}
                             onCardClick={props.onCardClick}
                             onCardLike={handleCardLike}>
                      </Card>)
            })}
        </section>
      </main>
    );
  }


  export default Main
