import React from 'react';
import avatarUser from '../images/jak_kusto.jpg'
import pencilLogo from '../images/pancil.svg'
import plusLogo from '../images/plus.svg'
import Card from './Card.js';
import {api} from '../utils/api.js'
import { CurrentUserContext } from '../contexts/CurrentUserContext';




function Main(props) {
  // подпишем компонент на контекст
  const currentUser = React.useContext(CurrentUserContext);





  function handleCardLike(card) {
    // проверка есть ли лайк на текущей карточке
    // возвращает тру если среди лайкoв есть лайк пользователя
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    console.log(isLiked)

    // если пользователь не поставил лайк, то мы должны его добавить
    if (!isLiked) {
      api
        .likeCard(card._id)
        .then((newCard) => {
          const newCards = cards.map((c) => (c._id === card._id ? newCard : c));
          setCards(newCards);
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      api
        .deleteLikeCard(card._id)
        .then((newCard) => {
          const newCards = cards.map((c) => (c._id === card._id ? newCard : c));
          setCards(newCards);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }


  function handleCardDelete(card) {
    api.deleteCards(card._id)
      .then(() => {
        const newCards = cards.filter((c) => c._id !== card._id);
        setCards(newCards);
      })
      .catch(err => {
        console.log(err)
      })
  }





    return (
        <main className="content">

        <section className="profile">
            <button className="profile__avatar-button" onClick={props.onEditAvatar}>
              <img src={currentUser.avatar} className="profile__avatar" alt="Аватар пользователя"/>
            </button>

          <div className="profile__info">
            <h1 className="profile__title">{currentUser.name}</h1>
              <button className="profile__button" type='button' onClick={props.onEditProfile}>
                <img src={pencilLogo} className="profile__image" alt="Карандаш"/>
              </button>          
            <p className="profile__subtitle">{currentUser.about}</p>
          </div>
          <button className="profile__addbutton" type='button' onClick={props.onAddPlace}>
                <img src={plusLogo} className="profile__image" alt="Плюс"/>
          </button>  
        </section>
        <section className="elements">
            {cards.map((item) => {
              return ( <Card key={item.id}
                             card={item}
                             onCardClick={props.onCardClick}
                             onCardLike={handleCardLike}
                             onCardDelete={handleCardDelete}>
                      </Card>)
            })}
        </section>
      </main>
    );
  }


  export default Main
