import React from 'react';
import PopupWithForm from './PopupWithForm.js';


function EditProfilePopup(props) {
    const [name, setName] = React.useState('');
    const [description, setDescription] = React.useState('');


    function handleNameChange(evt) {
        setName(evt.target.value)
    }


    function handleDescriptionChange(evt) {
        setDescription(evt.target.value)
    }





    return (
        <PopupWithForm name='_js_editor' title='Редактировать профиль' isOpen={props.isOpen} onClose={props.onClose} buttonText='Сохранить'>
          <input id="name-input" type="text" className="popup__input popup__input_info_name"  name="name"
                          placeholder="Ваше имя" value={name} onChange={handleNameChange}/>
          <span className='error name-input-error'></span>
          <input id="description-input" type="text" className="popup__input popup__input_info_job"
                          name="about" placeholder="Ваш род деятельности" value={description} onChange={handleDescriptionChange}/>
          <span className='error description-input-error'></span>
          </PopupWithForm>
    );
}


export default EditProfilePopup
